import React, { FC } from 'react';
import styles from './EmployeeItem.module.scss';
import Employee from '../../../../../models/Employee';
import {NavLink} from 'react-router-dom';
import { BASE_API_URL } from '../../../../../Utils/fetch';

interface EmployeeItemProps {
  employee: Employee;
}

const EmployeeItem: FC<EmployeeItemProps> = ({employee}) => {

  const imgSrc = `${BASE_API_URL}/images/${employee.imageName}`;

  return(

    <div className={styles.EmployeeItem__empCard}>
      <header className={styles.EmployeeItem__empHeader}>
        מס' עובד: {employee.id}
      </header>
      <div className={styles.EmployeeItem__empBody}>
        <span><b>שם מלא:</b> {employee.firstName} {employee.lastName}</span> <br />
        <span><b> תפקיד:</b> {employee.title}</span><br />
        <span> <b>מגורים:</b> {employee.country} | {employee.city}</span><br />
        <span><b> תאריך לידה:</b> {employee.birthDate}</span><br />
      </div>
      <div className={styles.ProductItem__link}>
            <NavLink to={`images/${employee.id}`}>
              <img src={imgSrc} alt={employee.firstName} />
            </NavLink>
          </div>
    </div>

    );
}

export default EmployeeItem;
