import React, { FC,useState,useEffect } from 'react';
import styles from './EmployeeDetails.module.scss';
import {getEmployee} from '../../../../../Utils/fetch';
import {deleteEmployee} from '../../../../../Utils/fetch';
import Employee from '../../../../../models/Employee';
import {useParams , useNavigate} from 'react-router-dom';
import {BASE_API_URL} from '../../../../../Utils/fetch';
import Loader from '../../../../Loader/Loader';
import {NavLink} from 'react-router-dom';


interface EmployeeDetailsProps {}

const EmployeeDetails: FC<EmployeeDetailsProps> = () => {

  const params = useParams();
  const navigate = useNavigate();
  const [employee,setEmployee] = useState<Employee>();
  const [loading,setLoading] = useState(true);

  const deleteEmployee = async () => {
    if(params.prodId){
      setLoading(true);
      try{
        const success = await deleteEmployee(+params.id);
        if(success) {
          alert('employee was deleted successfully!');
          navigate('/employees');
        }
      }catch(err){
        console.log(err);
        setLoading(false);
      }
    }
  }

  useEffect(() => {

    if(params.prodId){

      getEmployee(+params.prodId).then((employee) => {
        setEmployee(employee);
      }).catch((err) => {
        console.log(err.message);
      }).finally(() => {
        setLoading(false)
      });
    }  

  },[])



  const renderEmployee = () => {

    if(employee) {
      const imgSrc = `${BASE_API_URL}/images/${employee.imageName}`;
      return(
        <div>
          <div className={`Box ${styles.EmployeeDetails__content}`} style={{width:'50%'}}>
          <h2>כרטיס עובד</h2><hr />
          <header className={styles.EmployeeItem__empHeader}>
          <b>מס' עובד:</b> {employee.id}
      </header><br />
            <div><b>שם מלא: </b> {employee.firstName} {employee.lastName}</div>
            <div><b>תפקיד: </b> {employee.title}</div>
            <div><b>מגורים: </b> {employee.city} | {employee.country}</div>
            <div><b> תאריך לידה:</b> {employee.birthDate}</div><br />
            <div className={styles.EmployeeDetails}>
            <img src={imgSrc}  style={{width:'100%'}}/>
          </div>
          <div className={styles.EmployeeItem__options}>
            <NavLink to='#'><button>Edit</button></NavLink>
            <NavLink onClick={deleteEmployee} to='#'><button>Delete</button></NavLink>
            <NavLink to='/employees'><button>Back</button></NavLink>
          </div>
          </div>

        </div>
      )
    }

  }

  if(loading) return <Loader />

  return(
    <div className='empCard' style={{width:500}}>
      {renderEmployee()}
    </div>

  );
}

export default EmployeeDetails;
