import React, { FC, useState } from 'react';
import styles from './Employees.module.scss';
import Employee from '../../../../models/Employee';
import EmployeeItem from './EmployeeItem/EmployeeItem';
import {NavLink} from 'react-router-dom';
import AddEmployee from './EmployeeDetails/AddEmployee/AddEmployee';

interface EmployeesProps {
  employees: Employee[];
  onAddEmployee: (employee:Employee) => void;
}

const Employees: FC<EmployeesProps> = ({employees,onAddEmployee}) => {

  const [showAddedEmployee,setShowAddedEmployee] = useState(false);

  const addedEmployeesHandler = () => {
    setShowAddedEmployee((prevState) => !prevState);
 }

  const renderEmployees = () => {
    return employees.map((employee) =>{
       const {id} = employee;
       return <EmployeeItem key={id} employee={employee}/>
    })
  }

  return (
    <div className={styles.Employees}>
            <NavLink onClick={addedEmployeesHandler} to='#'>Add Employee</NavLink>
      {renderEmployees()}
      {showAddedEmployee && <AddEmployee onAddEmployee={onAddEmployee} onClose={addedEmployeesHandler}/>}

    </div>
  );
}

export default Employees;
