import React, { FC } from 'react';
import styles from './youtube.module.scss';

interface YoutubeProps {}

const Youtube: FC<YoutubeProps> = () => {
  const date = new Date();
  const today = date.getDay();
  const displayDiscount = () => {

    if(today == 1){

      return <span>today 25% off!!!</span>
    }
  }


  return(
    <div className={styles.Youtube}>
    <div>{displayDiscount()}</div>
  <iframe width="660" height="375" src="https://www.youtube.com/embed/XKVPu3i_3-8" title="YouTube video player" allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture; web-share"></iframe>

    </div>
  );
}

export default Youtube;
