import React, { FC,useState,useEffect } from 'react';
import styles from './EmployeeDetails.module.scss';
import {getEmployee} from '../../../../../Utils/fetch';
import Employee from '../../../../../models/Employee';
import {useParams} from 'react-router-dom';
import {BASE_API_URL} from '../../../../../Utils/fetch';
import Loader from '../../../../Loader/Loader';

interface EmployeeDetailsProps {}

const EmployeeDetails: FC<EmployeeDetailsProps> = () => {

  const params = useParams();
  const [employee,setEmployee] = useState<Employee>();
  const [loading,setLoading] = useState(true);


  useEffect(() => {

    if(params.prodId){

      getEmployee(+params.prodId).then((employee) => {
        setEmployee(employee);
      }).catch((err) => {
        console.log(err.message);
      }).finally(() => {
        setLoading(false)
      });
    }  

  },[])

  const renderEmployee = () => {

    if(employee) {
      const imgSrc = `${BASE_API_URL}/images/${employee.imageName}`;
      return(
        <div className={styles.EmployeeDetails__empCard}>
          <div className={`Box ${styles.EmployeeDetails__content}`}>
            <p>שם מלא: {employee.firstName} {employee.lastName}</p>
            <p>תפקיד: {employee.title}</p>
            <p>מגורים: {employee.city} | {employee.country}</p>
            <div className={styles.EmployeeDetails}>
            <img src={imgSrc} />
          </div>
          </div>

        </div>
      )
    }

  }

  if(loading) return <Loader />

  return(
    <div className={styles.EmployeeDetails__empCard}>
    <header className={styles.EmployeeDetails__header}>
      <h2 className='Box'>כרטיס עובד</h2>
    </header>
    <div className={styles.EmployeeDetails__cardDetails}>
      {renderEmployee()}
    </div>
    </div>

  );
}

export default EmployeeDetails;
